export const demonIdle = (animation) => {
    animation.create({
        key: "demonIdle",
        frames: [
            {key: "demonIdle1", frame: 0},
            {key: "demonIdle2", frame: 0},
            {key: "demonIdle3", frame: 0},
            {key: "demonIdle4", frame: 0},
        ],
        frameRate: 10,
        repeat: -1
    })
}