export const basicMovement = (figure) => {

figure.anims.create({
    key: 'left',
    frames: figure.anims.generateFrameNumbers(figure, { start: 0, end: 3 }),
    frameRate: 10,
    repeat: -1
});

figure.anims.create({
    key: 'turn',
    frames: [{ key: figure, frame: 0 }],
    frameRate: 10
});

figure.anims.create({
    key: 'right',
    frames: figure.anims.generateFrameNumbers(figure, { start: 0, end: 3 }),
    frameRate: 10,
    repeat: -1
});
    
}

