export const squidMovement = (figure) => {


figure.anims.create({
    key: 'left',
    // frames: figure.anims.generateFrameNumbers(figure, { start: 0, end: 3 }),
    frameRate: 10,
    repeat: -1
});

figure.anims.create({
    key: 'idle',
    frames: figure.anims.generateFrameNumbers(figure.name, {start: 0, end: 3}),
    frameRate: 10,
    repeat: -1
});

figure.anims.create({
    key: 'right',
    // frames: figure.anims.generateFrameNumbers(figure, { start: 0, end: 3 }),
    frameRate: 10,
    repeat: -1
});
    
}

