export const updateFrame = (figure, cursors, keyboard) => {



    // keyboard.on("keydown-F", (e) => {
    //     e.stopPropagation()
    //     e.preventDefault()
    //     console.log("Hello World!")
    // } )


    if (cursors.left.isDown) {
        figure.setVelocityX(-160);

        // figure.anims.play('left', true);
    }
    else if (cursors.right.isDown) {
        figure.setVelocityX(160);

        // figure.anims.play('right', true);
    }
    else {
        figure.setVelocityX(0);

        // figure.anims.play('turn');
    }

    if (cursors.space.isDown && figure.body.touching.down) {
        figure.setVelocityY(-330);
    }
}